const message = $input.first().json.message.text.trim().toUpperCase();
const tickerRegex = /^[A-Z]{1,5}$/;
const isValidFormat = tickerRegex.test(message);

if (!isValidFormat) {
  return [{
    json: {
      error: true,
      message: "Please provide a valid ticker symbol (1-5 letters, e.g., AAPL, TSLA)",
      chat_id: $input.first().json.message.chat.id
    }
  }];
}

return [{
  json: {
    ticker: message,
    chat_id: $input.first().json.message.chat.id,
    user_id: $input.first().json.message.from.id
  }
}];

const html = $input.first().json.body;
const ticker = $('process-ticker').first().json.ticker;

const newsRegex = /<h3[^>]*>.*?<a[^>]*href="([^"]*?)"[^>]*>(.*?)<\/a>.*?<\/h3>/gs;
let articles = [];
let match;

while ((match = newsRegex.exec(html)) !== null) {
  const url = match[1];
  const title = match[2].replace(/<[^>]*>/g, '').trim();

  if (title.length < 10 || title.toLowerCase().includes('more from')) {
    continue;
  }

  articles.push({
    title: title,
    url: url.startsWith('http') ? url : `https://finance.yahoo.com${url}`,
    source: 'Yahoo Finance'
  });

  if (articles.length >= 10) break;
}

if (articles.length === 0) {
  return [{
    json: {
      error: true,
      message: `Not enough news articles found for ${ticker}. Please try another ticker symbol.`,
      chat_id: $('process-ticker').first().json.chat_id
    }
  }];
}

return [{
  json: {
    ticker: ticker,
    articles: articles,
    article_count: articles.length,
    chat_id: $('process-ticker').first().json.chat_id
  }
}];

const articles = $input.first().json.articles;
const ticker = $input.first().json.ticker;
const detailedArticles = [];

for (let article of articles) {
  detailedArticles.push({
    title: article.title,
    content: article.title,
    url: article.url,
    source: article.source
  });
}

return [{
  json: {
    ticker: ticker,
    detailed_articles: detailedArticles,
    chat_id: $input.first().json.chat_id
  }
}];

