from transformers import AutoTokenizer, AutoModelForSequenceClassification, pipeline
import torch
import json
import numpy as np

articles_data = None
financial_data = None

for item in items:
    if 'detailed_articles' in item['json']:
        articles_data = item['json']
    elif 'current_price' in item['json']:
        financial_data = item['json']

if not articles_data or not financial_data:
    return [{
        'json': {
            'error': True,
            'message': 'Missing required data for sentiment analysis',
            'chat_id': items[0]['json']['chat_id']
        }
    }]

try:
    model_name = "ProsusAI/finbert"
    tokenizer = AutoTokenizer.from_pretrained(model_name)
    model = AutoModelForSequenceClassification.from_pretrained(model_name)

    sentiment_pipeline = pipeline(
        "sentiment-analysis",
        model=model,
        tokenizer=tokenizer,
        device=0 if torch.cuda.is_available() else -1
    )

    article_sentiments = []
    sentiment_scores = []

    for article in articles_data['detailed_articles']:
        text = article['title']

        if len(text) > 512:
            text = text[:512]

        result = sentiment_pipeline(text)[0]

        label_mapping = {
            'positive': 'positive',
            'negative': 'negative', 
            'neutral': 'neutral'
        }

        sentiment = label_mapping.get(result['label'].lower(), 'neutral')
        confidence = result['score']

        article_sentiments.append({
            'title': article['title'],
            'sentiment': sentiment,
            'confidence': round(confidence, 3),
            'url': article['url']
        })

        if sentiment == 'positive':
            sentiment_scores.append(confidence)
        elif sentiment == 'negative':
            sentiment_scores.append(-confidence)
        else:
            sentiment_scores.append(0)

    avg_sentiment_score = np.mean(sentiment_scores)
    positive_count = sum(1 for s in article_sentiments if s['sentiment'] == 'positive')
    negative_count = sum(1 for s in article_sentiments if s['sentiment'] == 'negative')
    neutral_count = sum(1 for s in article_sentiments if s['sentiment'] == 'neutral')

    if avg_sentiment_score > 0.1:
        overall_sentiment = 'positive'
    elif avg_sentiment_score < -0.1:
        overall_sentiment = 'negative'
    else:
        overall_sentiment = 'neutral'

    sentiment_analysis = {
        'ticker': articles_data['ticker'],
        'overall_sentiment': overall_sentiment,
        'sentiment_score': round(avg_sentiment_score, 3),
        'total_articles': len(article_sentiments),
        'positive_articles': positive_count,
        'negative_articles': negative_count,
        'neutral_articles': neutral_count,
        'article_sentiments': article_sentiments,
        'chat_id': articles_data['chat_id']
    }

    return [{'json': sentiment_analysis}]

except Exception as e:
    return [{
        'json': {
            'error': True,
            'message': f'Error in sentiment analysis: {str(e)}',
            'chat_id': articles_data['chat_id']
        }
    }]

