const sentimentData = $('sentiment-analysis').first().json;
const financialData = $('fetch-financial-data').first().json;

if (sentimentData.error || financialData.error) {
  return [{
    json: {
      error: true,
      message: sentimentData.message || financialData.message,
      chat_id: sentimentData.chat_id || financialData.chat_id
    }
  }];
}

function generateOutlook(sentiment, financialPerf, volatility) {
  let outlook = "";
  let rating = "";

  let perfCategory = "neutral";
  if (financialPerf > 5) perfCategory = "strong_positive";
  else if (financialPerf > 2) perfCategory = "positive";
  else if (financialPerf < -5) perfCategory = "strong_negative";
  else if (financialPerf < -2) perfCategory = "negative";

  let volCategory = "normal";
  if (volatility > 5) volCategory = "high";
  else if (volatility > 3) volCategory = "elevated";
  else if (volatility < 1) volCategory = "low";

  if (sentiment === "positive" && perfCategory.includes("positive")) {
    outlook = "ðŸ“ˆ BULLISH: Positive market sentiment aligns with strong stock performance. ";
    rating = "BUY";
  } else if (sentiment === "negative" && perfCategory.includes("negative")) {
    outlook = "ðŸ“‰ BEARISH: Negative market sentiment matches poor stock performance. ";
    rating = "SELL";
  } else if (sentiment === "positive" && perfCategory.includes("negative")) {
    outlook = "âš–ï¸ MIXED: Positive news sentiment contrasts with recent stock decline. Potential recovery opportunity. ";
    rating = "WATCH";
  } else if (sentiment === "negative" && perfCategory.includes("positive")) {
    outlook = "âš ï¸ CAUTIOUS: Stock performing well despite negative news sentiment. Monitor for potential reversal. ";
    rating = "HOLD";
  } else {
    outlook = "âž¡ï¸ NEUTRAL: Balanced sentiment and performance indicators. ";
    rating = "HOLD";
  }

  if (volCategory === "high") {
    outlook += "High volatility suggests increased risk. ";
  } else if (volCategory === "low") {
    outlook += "Low volatility indicates stability. ";
  }

  return { outlook, rating };
}

const weekChange = financialData.week_change_percent;
const volatility = financialData.volatility;
const { outlook, rating } = generateOutlook(sentimentData.overall_sentiment, weekChange, volatility);

const analysis = {
  ticker: financialData.ticker,
  company_name: financialData.company_name,
  analysis_date: new Date().toISOString().split('T')[0],

  financial_summary: {
    current_price: financialData.current_price,
    week_performance: `${weekChange > 0 ? '+' : ''}${weekChange}%`,
    volatility: `${volatility}%`,
    volume_status: financialData.latest_volume > financialData.average_volume ? "Above Average" : "Below Average"
  },

  sentiment_summary: {
    overall_sentiment: sentimentData.overall_sentiment.toUpperCase(),
    confidence_score: sentimentData.sentiment_score,
    articles_analyzed: sentimentData.total_articles,
    sentiment_breakdown: {
      positive: sentimentData.positive_articles,
      negative: sentimentData.negative_articles,
      neutral: sentimentData.neutral_articles
    }
  },

  investment_outlook: {
    rating: rating,
    outlook_description: outlook,
    key_factors: [
      `News sentiment is ${sentimentData.overall_sentiment}`,
      `Stock ${weekChange > 0 ? 'gained' : 'lost'} ${Math.abs(weekChange)}% this week`,
      `Volatility is ${volatility > 3 ? 'elevated' : 'normal'} at ${volatility}%`
    ]
  },

  recent_headlines: sentimentData.article_sentiments.slice(0, 3).map(article => ({
    title: article.title,
    sentiment: article.sentiment,
    confidence: article.confidence
  })),

  chat_id: financialData.chat_id
};

return [{ json: analysis }];

